document.addEventListener("DOMContentLoaded",(function(){const saveEntryPath=()=>{if(!localStorage.getItem("initialEntryPath")){const entryPath=window.location.pathname;localStorage.setItem("initialEntryPath",entryPath);console.log("Entry path saved:",entryPath)}};const modifySignupLinks=()=>{const allLinks=document.querySelectorAll("a");const entryPath=localStorage.getItem("initialEntryPath")||"/";allLinks.forEach((link=>{const href=link.getAttribute("href");if(href&&href.includes("https://domain.com/signup")){const cleanPath=entryPath.replace(/^\//,"");const utmSource=cleanPath||"homepage";const separator=href.includes("?")?"&":"?";const newHref=`${href}${separator}utm_source=${encodeURIComponent(utmSource)}`;link.setAttribute("href",newHref);console.log("Modified signup link:",newHref)}}))};saveEntryPath();modifySignupLinks()}));