document.addEventListener("DOMContentLoaded",(()=>{const statElements=document.querySelectorAll("[data-stat]");statElements.forEach((el=>{const finalText=el.textContent.trim();const regex=/^([^0-9]*)(\d+(?:\.\d+)?)([^0-9]*)$/;const matches=finalText.match(regex);if(matches){const prefix=matches[1];const numberValue=parseFloat(matches[2]);const suffix=matches[3];const duration=2e3;let startTime=null;function animate(timestamp){if(!startTime)startTime=timestamp;const progress=timestamp-startTime;let currentValue=Math.min(numberValue,progress/duration*numberValue);if(Number.isInteger(numberValue)){currentValue=Math.floor(currentValue)}else{currentValue=currentValue.toFixed(1)}el.textContent=`${prefix}${currentValue}${suffix}`;if(progress<duration){window.requestAnimationFrame(animate)}else{el.textContent=`${prefix}${numberValue}${suffix}`}}window.requestAnimationFrame(animate)}}))}));